mapper.select:
SELECT *
FROM (
    SELECT ROWNUM AS RNUM, Z.*
    FROM (
        SELECT orderNo, userNo, reviewtext, score
        FROM Review
        where userNo = #{userNo} AND isDelete = 0
        ORDER BY orderNo desc
    ) Z WHERE ROWNUM  <=  #{end}
) WHERE RNUM >= #{start}

SELECT *
FROM (
         SELECT ROWNUM AS RNUM, Z.*
         FROM (
                  SELECT orderNo, userNo, reviewtext, score
                  FROM Review
                  where orderNo = (select userNo
                                   from ORDERDETAIL o, auction a
                                   where o.AUCTIONNO = a.AUCTIONNO)
                    AND isDelete = 0
                  ORDER BY orderNo desc
              ) Z WHERE ROWNUM  <=  #{end}
     ) WHERE RNUM >= #{start}

SELECT orderNo, userNo, reviewText, score, isDelete
FROM Review
WHERE orderNo = #{orderNo} AND userNo = #{userNo}

mapper.delete:
UPDATE Review
SET isDelete = '1'
WHERE orderNo = #{orderNo} AND userNo = #{userNo}
